{
  "windwp/nvim-autopairs",
  event = "InsertEnter",
  config = function()
    local npairs = require("nvim-autopairs")
    npairs.setup({
      check_ts = true,
      ts_config = {
        lua = {'string'},
        javascript = {'template_string'},
        java = false,
      }
    })

    -- Rule for HTML tags
    local Rule = require('nvim-autopairs.rule')
    local ts_conds = require('nvim-autopairs.ts-conds')

    -- Add rule for HTML tags
    npairs.add_rules({
      Rule('<', '>', 'html'):with_pair(ts_conds.is_not_ts_node({'string', 'comment'})):with_move(function(opts)
        return opts.char == '>'
      end):with_cr(function()
        return '<CR><CR><Up><Tab>'
      end)
    })
  end
}

