vim.api.nvim_create_autocmd('LspAttach', {
  group = vim.api.nvim_create_augroup('my.lsp', {}),
  callback = function(args)
    local client = vim.lsp.get_client_by_id(args.data.client_id)
    if client:supports_method('textDocument/completion') then
      local chars = {}; for i = 32, 126 do
        local c = string.char(i)
        if not string.find("{}()[],<>;:> ", c, 1, true) then
          table.insert(chars, c)
        end
      end
      client.server_capabilities.completionProvider.triggerCharacters = chars
      vim.lsp.completion.enable(true, client.id, args.buf, { autotrigger = true })
    end

    if not client:supports_method('textDocument/willSaveWaitUntil')
        and client:supports_method('textDocument/formatting') then
      vim.api.nvim_create_autocmd('BufWritePre', {
        group = vim.api.nvim_create_augroup('my.lsp', { clear = false }),
        buffer = args.buf,
        callback = function()
          vim.lsp.buf.format({ bufnr = args.buf, id = client.id, timeout_ms = 1000 })
        end,
      })
    end

    pcall(vim.keymap.del, 'n', 'grn')
    pcall(vim.keymap.del, 'n', 'gra')
    pcall(vim.keymap.del, 'n', 'grr')
    pcall(vim.keymap.del, 'n', 'gri')

    vim.keymap.set('n', 'rn', vim.lsp.buf.rename, { desc = 'Rename Symbol' })
    vim.keymap.set('n', 'ca', vim.lsp.buf.code_action, { desc = '[C]ode [A]ction' })
    vim.keymap.set('n', 'gr', vim.lsp.buf.references, { desc = '[G]oto [R]eferences' })
    vim.keymap.set('n', 'gd', vim.lsp.buf.definition, { desc = '[G]oto [D]efinition' })
    vim.keymap.set('n', 'gi', vim.lsp.buf.implementation, { desc = '[G]oto [I]mplementation' })
    vim.keymap.set('n', 'gtd', vim.lsp.buf.implementation, { desc = '[G]oto [T]ype [D]efinition' })

    -- Hide completion menu when typing > (set after LSP attaches to override any LSP keymaps)
    vim.keymap.set('i', '>', function()
      print("KEYMAP TRIGGERED - > pressed")
      local pum_visible = vim.fn.pumvisible()
      print("pumvisible = " .. pum_visible)
      if pum_visible == 1 then
        print("Closing completion menu")
        return '<C-e>>'
      end
      print("Just inserting >")
      return '>'
    end, { expr = true, replace_keycodes = false, buffer = args.buf })
  end
})

