    if not original_map then
      for _, map in ipairs(global_mappings) do
        if map.lhs == '>' then
          original_map = map
          break
        end
      end
    end

    -- Create wrapper that hides completion menu
    vim.keymap.set('i', '>', function()
      -- Hide completion menu if visible
      local pum_was_visible = vim.fn.pumvisible() == 1
      if pum_was_visible then
        vim.api.nvim_feedkeys(vim.api.nvim_replace_termcodes('<C-e>', true, false, true), 'n', false)
      end

      -- Call original mapping if it exists, otherwise just insert >
      if original_map then
        if original_map.callback then
          original_map.callback()
        elseif original_map.rhs then
          vim.api.nvim_feedkeys(vim.api.nvim_replace_termcodes(original_map.rhs, true, false, true), 'n', false)
        end
      else
        vim.api.nvim_feedkeys('>', 'n', false)
      end
    end, { buffer = args.buf })
  end
})

